import { DemoChildGenerator } from "../children/demo-data-generators/demo-child-generator.service";
import { DemoDataGenerator } from "../../core/demo-data/demo-data-generator";
import { Injectable } from "@angular/core";
import { Child } from "../children/model/child";
import { faker } from "../../core/demo-data/faker";
import { AttendanceMonth } from "./model/attendance-month";
import { AttendanceDay, AttendanceStatus } from "./model/attendance-day";

interface AttendanceProfile {
  id: number;
  present: number;
  excused: number;
  late: number;
  absent: number;
}

const ATTENDANCE_PROFILES: AttendanceProfile[] = [
  {
    id: 0,
    present: 94,
    excused: 3,
    late: 1,
    absent: 2,
  },
  {
    id: 1,
    present: 80,
    excused: 5,
    late: 5,
    absent: 10,
  },
  {
    id: 2,
    present: 70,
    excused: 5,
    late: 10,
    absent: 15,
  },
  {
    id: 3,
    present: 50,
    excused: 5,
    late: 5,
    absent: 40,
  },
];

/**
 * Generate AttendanceMonth entities for the last 15 months
 * Builds upon the generated demo Child entities.
 */
@Injectable()
export class DemoAttendanceGenerator extends DemoDataGenerator<
  AttendanceMonth
> {
  /**
   * This function returns a provider object to be used in an Angular Module configuration:
   *   `providers: [DemoAttendanceGenerator.provider()]`
   */
  static provider() {
    return [
      { provide: DemoAttendanceGenerator, useClass: DemoAttendanceGenerator },
    ];
  }

  public static isHoliday(date: Date) {
    switch (date.getMonth()) {
      case 0:
        return (
          (date.getDate() >= 1 && date.getDate() <= 6) ||
          date.getDate() === 23 ||
          date.getDate() === 26
        );
      case 2:
        return (
          date.getDate() === 4 || (date.getDate() >= 20 && date.getDate() <= 21)
        );
      case 3:
        return (
          date.getDate() === 15 ||
          (date.getDate() >= 20 && date.getDate() <= 21)
        );
      case 4:
        return date.getDate() === 1 || date.getDate() >= 23;
      case 5:
        return date.getDate() <= 21;
      case 6:
        return date.getDate() === 4;
      case 7:
        return date.getDate() === 15;
      case 8:
        return date.getDate() === 11;
      case 9:
        return date.getDate() >= 5 && date.getDate() <= 16;
      case 10:
        return date.getDate() === 2 || date.getDate() === 10;
      case 11:
        return date.getDate() >= 24;
    }

    return false;
  }

  constructor(private demoChildren: DemoChildGenerator) {
    super();
  }

  generateEntities(): AttendanceMonth[] {
    const data = [];

    for (const child of this.demoChildren.entities) {
      data.push(...this.generateAttendanceRecordsForChild(child as Child));
    }

    return data;
  }

  private generateAttendanceRecordsForChild(child: Child): AttendanceMonth[] {
    const data = [];

    const finalMonth = faker.getEarlierDateOrToday(child.dropoutDate);
    finalMonth.setDate(1);

    const month = new Date();
    month.setDate(1);
    month.setMonth(month.getMonth() - 15);
    if (child.admissionDate > month) {
      month.setMonth(child.admissionDate.getMonth());
      month.setFullYear(child.admissionDate.getFullYear());
    }

    let attendanceProfile = undefined;
    while (month <= finalMonth) {
      attendanceProfile = this.selectNextAttendanceProfile(attendanceProfile);

      data.push(
        this.generateRecord(
          child,
          new Date(month.toISOString()),
          "school",
          attendanceProfile
        )
      );
      data.push(
        this.generateRecord(
          child,
          new Date(month.toISOString()),
          "coaching",
          attendanceProfile
        )
      );

      month.setMonth(month.getMonth() + 1);
    }

    return data;
  }

  private generateRecord(
    child: Child,
    month: Date,
    institution: string,
    attendanceProfile: AttendanceProfile
  ) {
    const attendanceMonth = new AttendanceMonth(faker.random.uuid());
    attendanceMonth.month = month;
    attendanceMonth.student = child.getId();
    attendanceMonth.institution = institution;
    attendanceMonth.dailyRegister.forEach((attendanceDay) =>
      this.setDayAttendance(
        attendanceDay,
        institution !== "school",
        attendanceProfile
      )
    );
    return attendanceMonth;
  }

  private setDayAttendance(
    attendanceDay: AttendanceDay,
    includeSaturday: boolean,
    attendanceProfile: AttendanceProfile
  ) {
    if (attendanceDay.date > new Date()) {
      return;
    }

    if (
      attendanceDay.date.getDay() === 0 || // Sunday
      (!includeSaturday && attendanceDay.date.getDay() === 6) // Saturday
    ) {
      return;
    }

    if (DemoAttendanceGenerator.isHoliday(attendanceDay.date)) {
      attendanceDay.status = AttendanceStatus.HOLIDAY;
      return;
    }

    const random = faker.random.number(100);
    if (random < attendanceProfile.present) {
      attendanceDay.status = AttendanceStatus.PRESENT;
    } else if (random < attendanceProfile.present + attendanceProfile.excused) {
      attendanceDay.status = AttendanceStatus.EXCUSED;
      attendanceDay.remarks = faker.random.arrayElement([
        "fever",
        "cough",
        "death in family",
        "family out of town",
      ]);
    } else if (
      random <
      attendanceProfile.present +
        attendanceProfile.excused +
        attendanceProfile.late
    ) {
      attendanceDay.status = AttendanceStatus.LATE;
    } else {
      attendanceDay.status = AttendanceStatus.ABSENT;
    }
  }

  /**
   * Select a new attendance profile (likelihood for absences, etc.).
   * It's most likely the profile will remain the same as in previous month.
   * @param previousAttendanceProfile
   */
  private selectNextAttendanceProfile(
    previousAttendanceProfile: AttendanceProfile
  ) {
    if (!previousAttendanceProfile) {
      return faker.random.arrayElement(ATTENDANCE_PROFILES);
    }

    if (faker.random.number(100) < 80) {
      return previousAttendanceProfile;
    } else {
      return faker.random.arrayElement(ATTENDANCE_PROFILES);
    }
  }
}

export interface BackgroundProcessState {
  /** unique name of the process as displayed to user */
  title: string;

  /**
   * specific details or context of the process.
   * this can serve as a "subtitle" for a common title of several similar processes also
   */
  details?: string;

  /** additional optional details explaining the process */
  description?: string;

  /** whether the process is still running */
  pending: boolean;

  /** if the process has failed, contains the error details; otherwise undefined */
  error?: any;
}

import {
  AfterViewInit,
  Component,
  OnInit,
  ViewChild,
  ElementRef,
} from "@angular/core";
import { Child } from "../model/child";
import { MatSort } from "@angular/material/sort";
import { MatTableDataSource } from "@angular/material/table";
import { ActivatedRoute, Router } from "@angular/router";
import { ChildrenService } from "../children.service";
import { AttendanceMonth } from "../../attendance/model/attendance-month";
import { FilterSelection } from "../../../core/filter/filter-selection/filter-selection";
import { MediaChange, MediaObserver } from "@angular/flex-layout";
import { MatPaginator, PageEvent } from "@angular/material/paginator";
import { UntilDestroy, untilDestroyed } from "@ngneat/until-destroy";
import { SessionService } from "../../../core/session/session-service/session.service";
import { User } from "../../../core/user/user";
import { EntityMapperService } from "../../../core/entity/entity-mapper.service";

export interface ColumnGroup {
  name: string;
  columns: string[];
}

@UntilDestroy()
@Component({
  selector: "app-children-list",
  templateUrl: "./children-list.component.html",
  styleUrls: ["./children-list.component.scss"],
})
export class ChildrenListComponent implements OnInit, AfterViewInit {
  childrenList: Child[] = [];
  attendanceList = new Map<string, AttendanceMonth[]>();
  childrenDataSource = new MatTableDataSource();

  listName: String;

  centerFS = new FilterSelection("center", []);
  dropoutFS = new FilterSelection("status", [
    {
      key: "active",
      label: "Current Project Children",
      filterFun: (c: Child) => c.isActive(),
    },
    {
      key: "dropout",
      label: "Dropouts",
      filterFun: (c: Child) => !c.isActive(),
    },
    { key: "", label: "All", filterFun: () => true },
  ]);
  filterSelections = [this.dropoutFS, this.centerFS];

  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;
  columnGroupSelection = "School Info";
  columnGroups: ColumnGroup[] = [
    {
      name: "Basic Info",
      columns: [
        "projectNumber",
        "name",
        "age",
        "gender",
        "schoolClass",
        "schoolId",
        "center",
        "status",
      ],
    },
    {
      name: "School Info",
      columns: [
        "projectNumber",
        "name",
        "age",
        "schoolClass",
        "schoolId",
        "attendance-school",
        "attendance-coaching",
        "motherTongue",
      ],
    },
    {
      name: "Status",
      columns: [
        "projectNumber",
        "name",
        "center",
        "status",
        "admissionDate",
        "has_aadhar",
        "has_kanyashree",
        "has_bankAccount",
        "has_rationCard",
        "has_bplCard",
      ],
    },
    {
      name: "Health",
      columns: [
        "projectNumber",
        "name",
        "center",
        "health_vaccinationStatus",
        "health_bloodGroup",
        "health_eyeHealthStatus",
        "health_LastEyeCheckup",
        "health_LastDentalCheckup",
        "health_LastENTCheckup",
        "health_LastVitaminD",
        "health_LastDeworming",
        "gender",
        "age",
        "dateOfBirth",
      ],
    },
    {
      name: "Mobile",
      columns: ["projectNumber", "name", "age", "schoolId"],
    },
  ];
  columnsToDisplay = ["projectNumber", "name"];
  filterString = "";

  public paginatorPageSize: number;
  public paginatorPageIndex: number;
  private user: User;

  /** dynamically calculated number of attendance blocks displayed in a column to avoid overlap */
  maxAttendanceBlocks: number = 3;
  @ViewChild("attendanceSchoolCell") schoolCell: ElementRef;
  @ViewChild("attendanceCoachingCell") coachingCell: ElementRef;

  constructor(
    private childrenService: ChildrenService,
    private router: Router,
    private route: ActivatedRoute,
    private media: MediaObserver,
    private sessionService: SessionService,
    private entityMapperService: EntityMapperService
  ) {}

  ngOnInit() {
    this.route.data.subscribe((config) => {
      this.listName = config.title;
    });
    this.loadData();
    this.loadUrlParams();
    this.user = this.sessionService.getCurrentUser();
    this.paginatorPageSize = this.user.paginatorSettingsPageSize.childrenList;
    this.paginatorPageIndex = this.user.paginatorSettingsPageIndex.childrenList;
    this.media.media$
      .pipe(untilDestroyed(this))
      .subscribe((change: MediaChange) => {
        switch (change.mqAlias) {
          case "xs":
          case "sm": {
            this.displayColumnGroup("Mobile");
            this.maxAttendanceBlocks = 1;
            break;
          }
          case "md": {
            this.displayColumnGroup("School Info");
            this.maxAttendanceBlocks = 2;
            break;
          }
          case "lg": {
            this.maxAttendanceBlocks = 3;
            break;
          }
          case "xl": {
            this.maxAttendanceBlocks = 6;
            break;
          }
        }
      });
  }

  private loadUrlParams(replaceUrl: boolean = false) {
    this.route.queryParams.subscribe((params) => {
      this.columnGroupSelection = params["view"]
        ? params["view"]
        : this.columnGroupSelection;
      this.displayColumnGroup(this.columnGroupSelection);

      this.filterSelections.forEach((f) => {
        f.selectedOption = params[f.name];
        if (f.selectedOption === undefined && f.options.length > 0) {
          f.selectedOption = f.options[0].key;
        }
      });
      this.applyFilterSelections(replaceUrl);
    });
  }

  ngAfterViewInit() {
    this.childrenDataSource.sort = this.sort;
    this.childrenDataSource.paginator = this.paginator;
    setTimeout(() => {
      this.paginator.pageIndex = this.paginatorPageIndex;
      this.paginator.page.next({
        pageIndex: this.paginator.pageIndex,
        pageSize: this.paginator.pageSize,
        length: this.paginator.length,
      });
    });
  }

  onPaginateChange(event: PageEvent) {
    this.paginatorPageSize = event.pageSize;
    this.paginatorPageIndex = event.pageIndex;
    this.updateUserPaginationSettings();
  }

  private updateUserPaginationSettings() {
    const hasChangesToBeSaved =
      this.paginatorPageSize !==
      this.user.paginatorSettingsPageSize.childrenList;

    this.user.paginatorSettingsPageIndex.childrenList = this.paginatorPageIndex;
    this.user.paginatorSettingsPageSize.childrenList = this.paginatorPageSize;

    if (hasChangesToBeSaved) {
      this.entityMapperService.save<User>(this.user);
    }
  }

  private loadData(replaceUrl: boolean = false) {
    this.childrenService
      .getChildren()
      .pipe(untilDestroyed(this))
      .subscribe((children) => {
        this.childrenList = children;
        const centers = children
          .map((c) => c.center)
          .filter((value, index, arr) => value && arr.indexOf(value) === index);
        this.centerFS.initOptions(centers, "center");

        this.applyFilterSelections(replaceUrl);
      });
    this.childrenService
      .getAttendances()
      .pipe(untilDestroyed(this))
      .subscribe((results) => this.prepareAttendanceData(results));
  }
  /*
  private initCenterFilterOptions(centersWithProbability: string[]) {
    const options = [{key: '', label: 'All', filterFun: (c: Child) => true}];

    centersWithProbability.forEach(center => {
      options.push({key: center.toLowerCase(), label: center, filterFun: (c: Child) => c.center === center});
    });

    this.centerFS.options = options;
  } */

  prepareAttendanceData(loadedEntities: AttendanceMonth[]) {
    this.attendanceList = new Map<string, AttendanceMonth[]>();
    loadedEntities.forEach((x) => {
      if (!this.attendanceList.has(x.student)) {
        this.attendanceList.set(x.student, new Array<AttendanceMonth>());
      }
      this.attendanceList.get(x.student).push(x);
    });

    this.attendanceList.forEach((studentsAttendance) => {
      studentsAttendance.sort((a, b) => {
        // descending by date
        if (a.month > b.month) {
          return -1;
        }
        if (a.month < b.month) {
          return 1;
        }
        return 0;
      });
    });
  }

  applyFilter(filterValue: string) {
    filterValue = filterValue.trim();
    filterValue = filterValue.toLowerCase(); // MatTableDataSource defaults to lowercase matches
    this.childrenDataSource.filter = filterValue;
  }

  displayColumnGroup(columnGroupName: string) {
    this.columnGroupSelection = columnGroupName;
    let found = false;
    let group: ColumnGroup;
    let i = 0;
    while (!found && i < this.columnGroups.length) {
      if (this.columnGroups[i].name === columnGroupName) {
        found = true;
        group = this.columnGroups[i];
      }
      i++;
    }
    this.columnsToDisplay = group.columns;
    this.updateUrl();
  }

  updateUrl(replaceUrl: boolean = false) {
    const params = {};
    this.filterSelections.forEach((f) => {
      params[f.name] = f.selectedOption;
    });

    params["view"] = this.columnGroupSelection;

    this.router.navigate(["child"], {
      queryParams: params,
      replaceUrl: replaceUrl,
    });
  }

  applyFilterSelections(replaceUrl: boolean = false) {
    let filteredData = this.childrenList;

    this.filterSelections.forEach((f) => {
      filteredData = filteredData.filter(f.getSelectedFilterFunction());
    });

    this.childrenDataSource.data = filteredData;

    this.updateUrl(replaceUrl);
  }
}

import { Component, Inject } from "@angular/core";
import { MAT_DIALOG_DATA, MatDialogRef } from "@angular/material/dialog";

/**
 * A configurable confirmation dialog box
 * used by the {@link ConfirmationDialogService}.
 */
@Component({
  selector: "app-confirmation-dialog",
  templateUrl: "./confirmation-dialog.component.html",
  styleUrls: ["./confirmation-dialog.component.scss"],
})
export class ConfirmationDialogComponent {
  /**
   * This component is used as a template for MatDialog, created with the required dependencies through that service.
   * @param dialogRef The reference to the dialog this component is displayed within
   * @param data The configuration defining what text and buttons will be displayed
   */
  constructor(
    public dialogRef: MatDialogRef<ConfirmationDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: ConfirmationDialogConfig
  ) {}
}

/**
 * Options to configure the {@link ConfirmationDialogComponent}.
 */
export interface ConfirmationDialogConfig {
  /** title of the dialog box */
  title: string;

  /** description text in the dialog box */
  text: string;

  /** Whether to display an option of yes/no buttons or just a single "ok" button */
  yesNo: boolean;
}

import { InjectionToken } from "@angular/core";

/**
 * Use this token to provide (and thereby register) custom implementations of {@link ConflictResolutionStrategy}.
 *
 * `{ provide: CONFLICT_RESOLUTION_STRATEGY, useClass: MyConflictResolutionStrategy, multi: true }`
 *
 * see {@link ConflictResolutionModule}
 */
export const CONFLICT_RESOLUTION_STRATEGY = new InjectionToken<
  ConflictResolutionStrategy
>("ConflictResolutionStrategy");

/**
 * Implement this interface to provide custom strategies how certain conflicts of an Entity type can be resolved automatically.
 *
 * see {@link ConflictResolutionModule}
 */
export interface ConflictResolutionStrategy {
  autoDeleteConflictingRevision(currentDoc: any, conflictingDoc: any): boolean;
}

export interface DynamicComponentConfig {
  component: string;
  config?: any;
}

export interface EntitySchemaField {
  /**
   * The datatype of this field. This will trigger to matching datatype transformer when saving/loading the entity.
   *
   * If you don't set this explicitly, the dataType is inferred from the TypeScript type of the property that is annotated.
   */
  dataType?: string;

  /**
   * In case of an array field (dataType==='array') define the datatype of the values contained in the array
   *
   * see {@link arrayDataType}
   */
  arrayDataType?: string;

  /**
   * Set to true to make the framework automatically create an index to retrieve/filter Entities quickly based on this field
   *
   * @todo not implemented yet
   */
  generateIndex?: boolean; // TODO: implement index support in EntitySchema

  /**
   * (Optional) Assign any custom "extension" configuration you need for a specific datatype extension.
   *
   * You can pass any kind of value here to allow complex custom datytypes' transformations
   * that are not part of the core datatypes and therefore not included in this core interface.
   */
  ext?: any;
}

export class FilterSelection<T> {
  /** key of currently selected option */
  public selectedOption: string;

  /**
   * Create a FilterSelection with different options to be selected.
   * @param name The name or id describing this filter
   * @param options An array of different filtering variants to chose between
   */
  constructor(
    public name: string,
    public options: FilterSelectionOption<T>[]
  ) {}

  /** default filter will keep all items in the result */
  defaultFilterFunction = (c: T) => true;

  /**
   * Get the full filter option by its key.
   * @param key The identifier of the requested option
   */
  getOption(key: string): FilterSelectionOption<T> {
    return this.options.find((option) => option.key === key);
  }

  private getFilterFunction(key: string) {
    const option = this.getOption(key);

    if (!option) {
      return this.defaultFilterFunction;
    } else {
      return option.filterFun;
    }
  }

  /**
   * Get the filter function of the currently selected option.
   * If no option has been selected yet, the returned filter function will keep all items in the result if applied.
   *
   * This can be directly used on a data array, e.g. "data.filter(filterSelection.getFilterFunction('a'))"
   */
  public getSelectedFilterFunction() {
    return this.getFilterFunction(this.selectedOption);
  }

  /**
   * Reset the available filter options to the ones dynamically generated from the given value to be matched.
   *
   * This is a utility function to make it easier to generate {@link FilterSelectionOption}s for standard cases
   * if you simply want each option to filter items having the given attribute matching different values.
   * If you have more sophisticated filtering needs, use the constructor to set {@link FilterSelectionOption}s that
   * you created yourself.
   *
   * @param valuesToMatchAsOptions An array of values to be matched.
   *        A separate FilterSelectionOption is created for each value with a filter
   *        that is true of a data item's property exactly matches that value.
   * @param attributeName The name of the property of a data item that is compared to the value in the filter function.
   */
  public initOptions(valuesToMatchAsOptions: any[], attributeName: string) {
    const options = [{ key: "", label: "All", filterFun: (e: T) => true }];

    valuesToMatchAsOptions.forEach((k) => {
      options.push({
        key: k.toLowerCase(),
        label: k.toString(),
        filterFun: (e: T) => e[attributeName] === k,
      });
    });

    this.options = options;
  }
}

/**
 * Represents one specific option to filter data in a certain way.
 * used by {@link FilterSelection}
 */
export interface FilterSelectionOption<T> {
  /** identifier for this option in the parent FilterSelection instance */
  key: string;

  /** label displayed for this option to the user in the UI */
  label: string;

  /**
   * function that filters a dataset.
   * This filter logic will be applied to data when this option is selected.
   */
  filterFun: (c: T) => boolean;
}

export interface FormValidationResult {
  /** whether validation has succeeded without errors */
  hasPassedValidation: boolean;

  /** message displayed to explain validation problems */
  validationMessage: string;
}

export interface IAppConfig {
  /** Title of the app overall */
  site_name: string;

  /** database configuration */
  database: {
    /** name of the database - both remote and local */
    name: string;

    /**
     * URL to the database server to be synced with
     *
     * Beware of CORS issues if this is on a different domain than the app is served from.
     */
    remote_url: string;

    /** timeout for remote connection attempts */
    timeout: number;

    /**
     * whether to use a temporary in-memory database instead of a persistant and synced PouchDB/CouchDB
     *
     * If this is set to "true" the {@link MockSessionService} and {@link MockDatabase} are used
     * and the {@link DemoDataModule} generates mock data.
     */
    useTemporaryDatabase: boolean;
  };

  /**
   * Optional configuration of a webdav (e.g. Nextcloud) integration
   */
  webdav?: {
    /**
     * URL to the webdav endpoint of the Nextcloud server
     *
     * Beware of CORS issues if this is on a different domain than the app is served from.
     */
    remote_url: string;
  };

  /**
   * Optional flag to activate additional debugging output to troubleshoot problems on a user's device
   */
  debug?: boolean;
}

