import { Inject, Injectable } from "@angular/core";
import {
  CONFLICT_RESOLUTION_STRATEGY,
  ConflictResolutionStrategy,
} from "./conflict-resolution-strategy";

/**
 * Attempt automatic conflict resolutions or identify trivial conflicts for semi-automatic resolution.
 */
@Injectable({
  providedIn: "root",
})
export class AutoResolutionService {
  constructor(
    @Inject(CONFLICT_RESOLUTION_STRATEGY)
    private resolutionStrategies: ConflictResolutionStrategy[]
  ) {}

  /**
   * Checks whether any registered resolution strategy suggests that the conflicting version should be automatically deleted.
   *
   * This method does not delete the conflict. It only suggests whether it should be deleted automatically.
   *
   * @param currentDoc The currently active revision of the doc
   * @param conflictingDoc The conflicting revision of the doc to be checked whether it can be deleted
   */
  public shouldDeleteConflictingRevision(
    currentDoc: any,
    conflictingDoc: any
  ): boolean {
    for (const resolutionStrategy of this.resolutionStrategies) {
      if (
        resolutionStrategy.autoDeleteConflictingRevision(
          currentDoc,
          conflictingDoc
        )
      ) {
        return true;
      }
    }

    return false;
  }
}

import { Injectable } from "@angular/core";
import { Database } from "../../database/database";
import { User } from "../../user/user";
import { Papa } from "ngx-papaparse";

/**
 * Create and load backups of the database.
 */
@Injectable({
  providedIn: "root",
})
export class BackupService {
  /** CSV row separator */
  static readonly SEPARATOR_ROW = "\n";
  /** CSV column/field separator */
  static readonly SEPARATOR_COL = ",";

  constructor(private db: Database, private papa: Papa) {}

  /**
   * Creates a List of JSON elements separated by `BackupService.SEPARATOR_ROW` holding all elements of the database.
   * This method can be used to created a backup of the data.
   *
   * @returns Promise<string> a string containing all elements of the database separated by SEPARATOR_ROW
   */
  getJsonExport(): Promise<string> {
    return this.db.getAll().then((results) => this.createJson(results));
  }

  /**
   * Creates a JSON string of the given data.
   *
   * @param data the data which should be converted to JSON
   * @returns string containing all the values stringified elements of the input data
   */
  createJson(data): string {
    let res = "";
    data.forEach((r) => {
      res += JSON.stringify(r) + BackupService.SEPARATOR_ROW;
    });

    return res.trim();
  }

  /**
   * Creates a CSV string of the whole database
   *
   * @returns string a valid CSV string
   */
  getCsvExport(): Promise<string> {
    return this.db.getAll().then((results) => this.createCsv(results));
  }

  /**
   * Creates a CSV string of the input data
   *
   * @param data an array of elements
   * @returns string a valid CSV string of the input data
   */
  createCsv(data): string {
    // create list of row descriptions for the csv string
    const allFields = ["_id", "_rev"];
    data.forEach((element) => allFields.push(...Object.keys(element)));
    const uniqueFields = [...new Set(allFields)]; // creates list with unique elements

    return this.papa.unparse(
      { data: data, fields: uniqueFields },
      { quotes: true, header: true }
    );
  }

  /**
   * Removes all but the user entities of the database
   *
   * @returns Promise<any> a promise that resolves after all remove operations are done
   */
  clearDatabase(): Promise<any> {
    const userEntityPrefix = new User("").getType() + ":";

    return this.db.getAll().then((allDocs) => {
      const p = [];
      allDocs.forEach((row) => {
        if (!row._id.startsWith(userEntityPrefix)) {
          // skip User entities in order to not break login!
          p.push(this.db.remove(row));
        }
      });
      return Promise.all(p);
    });
  }

  /**
   * Fills the database with the provided JSON data.
   * Data should be generated by the `getJsonExport` function
   *
   * @param json An array of entities to be written to the database
   * @param forceUpdate should existing objects be overridden? Default false
   *
   * @returns Promise<any> a promise that resolves after all save operations are done
   */
  importJson(json, forceUpdate = false) {
    const promises = [];
    json.split(BackupService.SEPARATOR_ROW).forEach((record) => {
      promises.push(this.db.put(JSON.parse(record), forceUpdate));
    });
    return Promise.all(promises);
  }

  /**
   * Fills the database with the elements of the CSV string
   *
   * @param csv a valid CSV string
   * @param forceUpdate should existing elements be overridden? Default false
   *
   * @returns Promise<any> a promise that resolves after all save operations are done
   */
  importCsv(csv, forceUpdate = false): Promise<any> {
    const promises = [];

    const parsedCsv = this.papa.parse(csv, {
      header: true,
      dynamicTyping: true,
      skipEmptyLines: true,
    });
    parsedCsv.data.forEach((record) => {
      // remove undefined properties
      for (const propertyName in record) {
        if (record[propertyName] === null) {
          delete record[propertyName];
        }
      }

      promises.push(this.db.put(record, forceUpdate));
    });

    return Promise.all(promises);
  }
}

import { Injectable } from "@angular/core";
import { SafeUrl } from "@angular/platform-browser";
import { CloudFileService } from "../../../core/webdav/cloud-file-service.service";
import { Child } from "../model/child";
import { BehaviorSubject } from "rxjs";

@Injectable({
  providedIn: "root",
})
export class ChildPhotoService {
  private basePath = "photos/";

  constructor(private cloudFileService: CloudFileService) {}

  /**
   * Creates an ArrayBuffer of the photo for that Child or the default image url.
   * @param child
   */
  public async getImage(child: {
    entityId: string;
    photoFile?: string;
  }): Promise<SafeUrl> {
    let image = await this.getImageFromCloudService(child);
    if (!image) {
      image = this.getImageFromAssets(child);
    }
    return image;
  }

  private async getImageFromCloudService(child: {
    entityId: string;
  }): Promise<SafeUrl> {
    let image;
    if (this.cloudFileService.isConnected()) {
      const imageType = [".png", ".jpg", ".jpeg", ""];
      for (const ext of imageType) {
        const filepath = this.basePath + child.entityId + ext;
        try {
          image = await this.cloudFileService.getFile(filepath);
          break;
        } catch (err) {
          if (err === "File not found") {
            // ignore and try next file extension
          } else {
            console.warn("Error loading image: ", err);
          }
        }
      }
    }
    return image;
  }

  private getImageFromAssets(child: { photoFile?: string }): SafeUrl {
    if (!child.photoFile || child.photoFile.trim() === "") {
      return this.getDefaultImage();
    }
    return Child.generatePhotoPath(child.photoFile);
  }

  private getDefaultImage(): SafeUrl {
    return "assets/child.png";
  }

  /**
   * Load the image for the given child asynchronously, immediately returning an Observable
   * that initially emits the static image and later resolves to the image from the cloud service if one exists.
   * This allows to immediately display a proper placeholder while the loading may take some time.
   * @param child The Child instance for which the photo should be loaded.
   */
  public getImageAsyncObservable(child: {
    entityId: string;
    photoFile?: string;
  }): BehaviorSubject<SafeUrl> {
    const resultSubject = new BehaviorSubject(this.getImageFromAssets(child));
    this.getImageFromCloudService(child).then((photo) => {
      if (photo && photo !== resultSubject.value) {
        resultSubject.next(photo);
      }
      resultSubject.complete();
    });
    return resultSubject;
  }

  /**
   * Check if saving/uploading images is supported in the current state.
   */
  public canSetImage(): boolean {
    return this.cloudFileService.isConnected();
  }

  /**
   * Uploads a given image through the CloudFileService if connected.
   * @param imageFile Image to be stored
   * @param childId Id of child for which one wants to store the image
   */
  public async setImage(imageFile: any, childId: string): Promise<void> {
    if (!this.canSetImage()) {
      return Promise.reject("CloudFileService not connected.");
    }

    const fileExt = imageFile.name.substr(imageFile.name.lastIndexOf("."));
    return this.cloudFileService.uploadFile(
      imageFile,
      this.basePath + childId + fileExt
    );
  }
}

import { Injectable } from "@angular/core";
import { EntityMapperService } from "../../entity/entity-mapper.service";
import { HttpClient } from "@angular/common/http";
import { Child } from "../../../child-dev-project/children/model/child";

/**
 * Utility service to automatically detect and update filenames for Child entities' photos.
 */
@Injectable({
  providedIn: "root",
})
export class ChildPhotoUpdateService {
  constructor(
    private entityService: EntityMapperService,
    private httpClient: HttpClient
  ) {}

  /**
   * Tries to detect and update the filename of an existing photo for all Child entities,
   * saving the entities that were updated.
   */
  public async updateChildrenPhotoFilenames() {
    const children = await this.entityService.loadType<Child>(Child);
    for (const child of children) {
      await this.updatePhotoIfFileExists(child, `${child.projectNumber}.png`);
      await this.updatePhotoIfFileExists(child, `${child.projectNumber}.jpg`);
    }
  }

  /**
   * Check (and if it exists update) the Child's photo file.
   * @param child The child to be updated
   * @param filename A guess for a likely filename that needs to be checked
   */
  private async updatePhotoIfFileExists(child: Child, filename: string) {
    if (child.photoFile && child.photoFile !== "") {
      // do not overwrite existing path
      return;
    }

    const fileExists = await this.checkIfFileExists(
      Child.generatePhotoPath(filename)
    );
    if (fileExists) {
      child.photoFile = filename;
      this.entityService.save<Child>(child);
      console.log(
        `set photoFile for Child:${child.getId()} (${
          child.projectNumber
        }) to ${filename}`
      );
    }
  }

  private async checkIfFileExists(filename): Promise<boolean> {
    try {
      await this.httpClient.get(filename).toPromise();
      return true;
    } catch (e) {
      if (e.status === 200) {
        return true;
      }
      return false;
    }
  }
}

import { Injectable } from "@angular/core";
import { from, Observable, Subject } from "rxjs";
import { Child } from "./model/child";
import { EntityMapperService } from "../../core/entity/entity-mapper.service";
import { AttendanceMonth } from "../attendance/model/attendance-month";
import { Note } from "../notes/model/note";
import { EducationalMaterial } from "../educational-material/model/educational-material";
import { Aser } from "../aser/model/aser";
import { ChildSchoolRelation } from "./model/childSchoolRelation";
import { HealthCheck } from "../health-checkup/model/health-check";
import { EntitySchemaService } from "../../core/entity/schema/entity-schema.service";
import { ChildPhotoService } from "./child-photo-service/child-photo.service";
import { LoadChildPhotoEntitySchemaDatatype } from "./child-photo-service/datatype-load-child-photo";
import moment from "moment";
import * as uniqid from "uniqid";
import { LoggingService } from "../../core/logging/logging.service";
import { DatabaseIndexingService } from "../../core/entity/database-indexing/database-indexing.service";

@Injectable()
export class ChildrenService {
  constructor(
    private entityMapper: EntityMapperService,
    private entitySchemaService: EntitySchemaService,
    private dbIndexing: DatabaseIndexingService,
    childPhotoService: ChildPhotoService,
    private logger: LoggingService
  ) {
    this.entitySchemaService.registerSchemaDatatype(
      new LoadChildPhotoEntitySchemaDatatype(childPhotoService)
    );
    this.createDatabaseIndices();
  }

  public createDatabaseIndices() {
    this.createAttendanceAnalysisIndex();
    this.createNotesIndex();
    this.createAttendancesIndex();
    this.createChildSchoolRelationIndex();
  }

  /**
   * returns an observable which retrieves children from the database and loads their pictures
   */
  getChildren(): Observable<Child[]> {
    const results = new Subject<Child[]>();

    this.entityMapper.loadType<Child>(Child).then(async (loadedChildren) => {
      results.next(loadedChildren);

      for (const loadedChild of loadedChildren) {
        const childCurrentSchoolInfo = await this.getCurrentSchoolInfoForChild(
          loadedChild.getId()
        );
        await this.migrateToNewChildSchoolRelationModel(
          loadedChild,
          childCurrentSchoolInfo
        );
        loadedChild.schoolClass = childCurrentSchoolInfo.schoolClass;
        loadedChild.schoolId = childCurrentSchoolInfo.schoolId;
      }
      results.next(loadedChildren);
      results.complete();
    });

    return results;
  }

  /**
   * DATA MODEL UPGRADE
   * Check if the Child Entity still contains direct links to schoolId and schoolClass
   * and create a new ChildSchoolRelation if necessary.
   * @param loadedChild Child entity to be checked and migrated
   * @param childCurrentSchoolInfo Currently available school information according to new data model from ChildSchoolRelation entities
   */
  private async migrateToNewChildSchoolRelationModel(
    loadedChild: Child,
    childCurrentSchoolInfo: { schoolId: string; schoolClass: string }
  ) {
    if (!loadedChild.schoolClass && !loadedChild.schoolId) {
      // no data from old model -> skip migration
      return;
    }

    if (
      loadedChild.schoolId !== childCurrentSchoolInfo.schoolId ||
      loadedChild.schoolClass !== childCurrentSchoolInfo.schoolClass
    ) {
      // generate a ChildSchoolRelation entity from the information of the previous data model
      const autoMigratedChildSchoolRelation = new ChildSchoolRelation(uniqid());
      autoMigratedChildSchoolRelation.childId = loadedChild.getId();
      autoMigratedChildSchoolRelation.schoolId = loadedChild.schoolId;
      autoMigratedChildSchoolRelation.schoolClass = loadedChild.schoolClass;
      await this.entityMapper.save(autoMigratedChildSchoolRelation);
      this.logger.debug(
        "migrated Child entity to new ChildSchoolRelation model " +
          loadedChild._id
      );
      console.log(autoMigratedChildSchoolRelation);
    }

    // save the Child entity to remove the deprecated attributes from the doc in the database
    await this.entityMapper.save(loadedChild);
  }

  /**
   * returns an observable which retrieves a single child and loads its photo
   * @param id id of child
   */
  getChild(id: string): Observable<Child> {
    const promise = this.entityMapper
      .load<Child>(Child, id)
      .then((loadedChild) => {
        return this.getCurrentSchoolInfoForChild(id).then(
          (currentSchoolInfo) => {
            loadedChild.schoolClass = currentSchoolInfo.schoolClass;
            loadedChild.schoolId = currentSchoolInfo.schoolId;
            return loadedChild;
          }
        );
      });
    return from(promise);
  }

  getAttendances(): Observable<AttendanceMonth[]> {
    return from(this.entityMapper.loadType<AttendanceMonth>(AttendanceMonth));
  }

  getAttendancesOfChild(childId: string): Observable<AttendanceMonth[]> {
    const promise = this.dbIndexing
      .queryIndex("attendances_index/by_child", {
        key: childId,
        include_docs: true,
      })
      .then((loadedEntities) => {
        return loadedEntities.rows.map((loadedRecord) => {
          const entity = new AttendanceMonth("");
          this.entitySchemaService.loadDataIntoEntity(entity, loadedRecord.doc);
          return entity;
        });
      });

    return from(promise);
  }

  getAttendancesOfMonth(month: Date): Observable<AttendanceMonth[]> {
    const monthString =
      month.getFullYear().toString() + "-" + (month.getMonth() + 1).toString();
    const promise = this.dbIndexing
      .queryIndex("attendances_index/by_month", {
        key: monthString,
        include_docs: true,
      })
      .then((loadedEntities) => {
        return loadedEntities.rows.map((loadedRecord) => {
          const entity = new AttendanceMonth("");
          this.entitySchemaService.loadDataIntoEntity(entity, loadedRecord.doc);
          return entity;
        });
      });

    return from(promise);
  }

  private createAttendancesIndex(): Promise<any> {
    const designDoc = {
      _id: "_design/attendances_index",
      views: {
        by_child: {
          map:
            "(doc) => { " +
            'if (!doc._id.startsWith("' +
            AttendanceMonth.ENTITY_TYPE +
            '")) return;' +
            "emit(doc.student); " +
            "}",
        },
        by_month: {
          map:
            "(doc) => { " +
            'if (!doc._id.startsWith("' +
            AttendanceMonth.ENTITY_TYPE +
            '")) return;' +
            "emit(doc.month); " +
            "}",
        },
      },
    };

    return this.dbIndexing.createIndex(designDoc);
  }

  private createChildSchoolRelationIndex(): Promise<any> {
    const designDoc = {
      _id: "_design/childSchoolRelations_index",
      views: {
        by_child: {
          map: `(doc) => {
            if (!doc._id.startsWith("${ChildSchoolRelation.ENTITY_TYPE}")) return;
            emit(doc.childId);
            }`,
        },
        by_school: {
          map: `(doc) => {
            if ( (!doc._id.startsWith("${ChildSchoolRelation.ENTITY_TYPE}")) ||
                (doc.start && (new Date(doc.start) > new Date().setHours(0, 0, 0, 0))) ||
                (doc.end && (new Date(doc.end) < new Date().setHours(0, 0, 0, 0))) ) {
              return;
            }
            emit(doc.schoolId);
            }`,
        },
        by_date: {
          map: `(doc) => {
            if (!doc._id.startsWith("${ChildSchoolRelation.ENTITY_TYPE}")) return;
            let timestamp = (new Date(doc.start || '3000-01-01')).getTime().toString().padStart(14, "0");
            emit(doc.childId + '_' + timestamp);
            }`,
        },
      },
    };
    return this.dbIndexing.createIndex(designDoc);
  }

  queryLatestRelation(childId: string): Promise<ChildSchoolRelation> {
    return this.querySortedRelations(childId, 1).then(
      (children) => children[0]
    );
  }

  querySortedRelations(
    childId: string,
    limit?: number
  ): Promise<ChildSchoolRelation[]> {
    const options: any = {
      startkey: childId + "_\uffff", //  higher value needs to be startkey
      endkey: childId + "_", //  \uffff is not a character -> only relations staring with childId will be selected
      descending: true,
      include_docs: true,
      limit: limit,
    };
    return this.dbIndexing
      .queryIndex("childSchoolRelations_index/by_date", options)
      .then((loadedEntities) => {
        return loadedEntities.rows.map((loadedRecord) => {
          const entity = new ChildSchoolRelation("");
          this.entitySchemaService.loadDataIntoEntity(entity, loadedRecord.doc);
          return entity;
        });
      });
  }

  queryRelationsOf(
    queryType: "child" | "school",
    id: string
  ): Promise<ChildSchoolRelation[]> {
    return this.dbIndexing
      .queryIndex("childSchoolRelations_index/by_" + queryType, {
        key: id,
        include_docs: true,
      })
      .then((loadedEntities) => {
        return loadedEntities.rows.map((loadedRecord) => {
          const entity = new ChildSchoolRelation("");
          this.entitySchemaService.loadDataIntoEntity(entity, loadedRecord.doc);
          return entity;
        });
      });
  }

  queryAttendanceLast3Months() {
    return this.dbIndexing.queryIndex("avg_attendance_index/three_months", {
      reduce: true,
      group: true,
    });
  }

  queryAttendanceLastMonth() {
    return this.dbIndexing.queryIndex("avg_attendance_index/last_month", {
      reduce: true,
      group: true,
    });
  }

  private createAttendanceAnalysisIndex(): Promise<any> {
    const designDoc = {
      _id: "_design/avg_attendance_index",
      views: {
        three_months: {
          map: this.getAverageAttendanceMapFunction(),
          reduce: "_stats",
        },
        last_month: {
          map: this.getLastAverageAttendanceMapFunction(),
          reduce: "_stats",
        },
      },
    };

    return this.dbIndexing.createIndex(designDoc);
  }

  private getAverageAttendanceMapFunction() {
    return (
      "(doc) => {" +
      'if (!doc._id.startsWith("AttendanceMonth:") ) { return; }' +
      "if (!isWithinLast3Months(new Date(doc.month), new Date())) { return; }" +
      "var attendance = (doc.daysAttended / (doc.daysWorking - doc.daysExcused));" +
      "if (!Number.isNaN(attendance)) { emit(doc.student, attendance); }" +
      "function isWithinLast3Months(date, now) {" +
      "  let months;" +
      "  months = (now.getFullYear() - date.getFullYear()) * 12;" +
      "  months -= date.getMonth();" +
      "  months += now.getMonth();" +
      "  if (months < 0) { return false; }" +
      "  return months <= 3;" +
      "}" +
      "}"
    );
  }

  private getLastAverageAttendanceMapFunction() {
    return (
      "(doc) => {" +
      'if (!doc._id.startsWith("AttendanceMonth:")) { return; }' +
      "if (!isWithinLastMonth(new Date(doc.month), new Date())) { return; }" +
      "var attendance = (doc.daysAttended / (doc.daysWorking - doc.daysExcused));" +
      "if (!Number.isNaN(attendance)) { emit(doc.student, attendance); }" +
      "function isWithinLastMonth(date, now) {" +
      "  let months;" +
      "  months = (now.getFullYear() - date.getFullYear()) * 12;" +
      "  months -= date.getMonth();" +
      "  months += now.getMonth();" +
      "  return months === 1;" +
      "}" +
      "}"
    );
  }

  getNotesOfChild(childId: string): Observable<Note[]> {
    const promise = this.dbIndexing
      .queryIndex("notes_index/by_child", { key: childId, include_docs: true })
      .then((loadedEntities) => {
        return loadedEntities.rows.map((loadedRecord) => {
          const entity = new Note("");
          this.entitySchemaService.loadDataIntoEntity(entity, loadedRecord.doc);
          return entity;
        });
      });

    return from(promise);
  }

  /**
   * Query how many days ago the last note for each child was added.
   *
   * Warning: Children without any notes will be missing from this map.
   *
   * @return A map of childIds as key and days since last note as value
   */
  public async getDaysSinceLastNoteOfEachChild(): Promise<Map<string, number>> {
    const stats = await this.dbIndexing.queryIndex(
      "notes_index/note_date_in_days_for_child",
      { reduce: true, group: true }
    );

    const results = new Map();
    for (const childStats of stats.rows) {
      const dateOfLatestNoteInDays = childStats.value.max;
      const todayInDays = new Date().getTime() / 86400000; // ms/day: 1000*60*60*24 = 86400000
      const daysSinceLastNote = todayInDays - dateOfLatestNoteInDays;
      results.set(childStats.key, daysSinceLastNote);
    }

    return results;
  }

  private createNotesIndex(): Promise<any> {
    const designDoc = {
      _id: "_design/notes_index",
      views: {
        by_child: {
          map:
            "(doc) => { " +
            'if (!doc._id.startsWith("' +
            Note.ENTITY_TYPE +
            '")) return;' +
            "if (!Array.isArray(doc.children)) return;" +
            "doc.children.forEach(childId => emit(childId)); " +
            "}",
        },
        note_date_in_days_for_child: {
          map:
            "(doc) => { " +
            'if (!doc._id.startsWith("' +
            Note.ENTITY_TYPE +
            '")) return;' +
            "if (!Array.isArray(doc.children) || !doc.date) return;" +
            "doc.children.forEach(childId => emit(childId, (new Date(doc.date)).getTime()/86400000)); " + // ms/day: 1000*60*60*24 = 86400000
            "}",
          reduce: "_stats",
        },
      },
    };

    return this.dbIndexing.createIndex(designDoc);
  }

  getEducationalMaterialsOfChild(
    childId: string
  ): Observable<EducationalMaterial[]> {
    return from(
      this.entityMapper
        .loadType<EducationalMaterial>(EducationalMaterial)
        .then((loadedEntities) => {
          return loadedEntities.filter((o) => o.child === childId);
        })
    );
  }

  /**
   *
   * @param childId should be set in the specific components and is passed by the URL as a parameter
   * This function should be considered refactored and should use a index, once they're made generic
   */
  getHealthChecksOfChild(childId: string): Observable<HealthCheck[]> {
    return from(
      this.entityMapper
        .loadType<HealthCheck>(HealthCheck)
        .then((loadedEntities) => {
          return loadedEntities.filter((h) => h.child === childId);
        })
    );
  }

  getAserResultsOfChild(childId: string): Observable<Aser[]> {
    return from(
      this.entityMapper.loadType<Aser>(Aser).then((loadedEntities) => {
        return loadedEntities.filter((o) => o.child === childId);
      })
    );
  }

  async getCurrentSchoolInfoForChild(
    childId: string
  ): Promise<{ schoolId: string; schoolClass: string }> {
    const relations = (await this.querySortedRelations(childId)) || [];
    for (const rel of relations) {
      if (
        moment(rel.start).isSameOrBefore(moment(), "days") &&
        moment(rel.end).isSameOrAfter(moment(), "days")
      ) {
        return {
          schoolId: rel.schoolId,
          schoolClass: rel.schoolClass,
        };
      }
    }

    return {
      schoolId: null,
      schoolClass: null,
    };
  }

  async getSchoolsWithRelations(
    childId: string
  ): Promise<ChildSchoolRelation[]> {
    return await this.querySortedRelations(childId);
  }
}

import { Injectable } from "@angular/core";
import { AppConfig } from "../app-config/app-config";
import webdav from "webdav";
import { DomSanitizer, SafeUrl } from "@angular/platform-browser";
import { SessionService } from "../session/session-service/session.service";

/**
 * Connect and access a remote cloud file system like Nextcloud
 * to upload and download files.
 *
 * Connection requires setup of the server in the AppConfig
 * as well as valid user credentials for that server in the currently logged in user entity.
 */
@Injectable()
export class CloudFileService {
  // TODO Check connection/login success?
  private client: any;
  private basePath: string;
  private fileList: string;
  private currentlyGettingList: Promise<boolean>;

  /**
   * Construct the service and immediately attempt to connect to the server with the current user.
   * @param domSanitizer
   * @param sessionService
   */
  constructor(
    private domSanitizer: DomSanitizer,
    private sessionService: SessionService
  ) {
    this.connect();
  }

  /**
   * (Re)-initialize the client connecting to the webdav server.
   * @param username Optional webdav username, otherwise the one set in the current user entity is used.
   * @param password Optional webdav password, otherwise the one set in the current user entity is used.
   */
  public async connect(username: string = null, password: string = null) {
    if (!AppConfig.settings.webdav || !this.sessionService.getCurrentUser()) {
      return;
    }

    this.reset();

    const currentUser = this.sessionService.getCurrentUser();
    this.basePath = currentUser.cloudBaseFolder;

    if (username === null && password == null) {
      username = currentUser.cloudUserName;
      password = currentUser.cloudPasswordDec;
    }

    if (!username || !password) {
      // abort if account is not configured
      this.client = null;
      return;
    }

    this.client = await webdav.createClient(
      AppConfig.settings.webdav.remote_url,
      {
        username: username,
        password: password,
      }
    );
  }

  /**
   * Reset the current state and requests.
   * e.g. clear promise that retrieves the root dir
   */
  private reset() {
    this.currentlyGettingList = null;
    this.fileList = null;
  }

  /**
   * checkConnection
   *
   * tries to upload and redownload a file.
   */
  public async checkConnection(): Promise<boolean> {
    // delete 'tmp.txt' if it exists
    const fileName: string = this.basePath + "/test.txt";
    if (await this.doesFileExist(fileName)) {
      await this.client.deleteFile(fileName);
    }

    await this.client.putFileContents(fileName, "TestString");
    const buffer = await this.client.getFileContents(fileName);
    const tmpContent = String.fromCharCode.apply(null, new Uint8Array(buffer));
    await this.client.deleteFile(fileName);

    if (tmpContent === "TestString") {
      return true;
    }
    return false;
  }

  /**
   * Returns the content path.
   * @param path File path relative to the base path, without leading slash; example 'folder1'
   */
  public async getDir(path: string): Promise<string> {
    const contents = await this.client.getDirectoryContents(
      this.basePath + path
    );
    return JSON.stringify(contents, undefined, 4);
  }

  /**
   * Checks if the file exists in the root directory.
   * @param name file name to check
   */
  public async doesFileExist(name: string): Promise<boolean> {
    // TODO: doesFileExist seems to have problems at least with nested files?

    if (!this.fileList && !this.currentlyGettingList) {
      // create promise that resolves when the file list is loaded
      // if this function gets called mulitple times this ensures that the list will only be loaded once
      this.currentlyGettingList = new Promise((resolve) => {
        this.getDir("").then((list) => {
          this.fileList = list;
          resolve(true);
        });
      });
    }
    if (!this.fileList) {
      await this.currentlyGettingList;
    }
    // hacky way of checking if file exists, subject to change
    // TODO fix this
    if (this.fileList.includes('"basename": "' + name.split("/").pop() + '"')) {
      return true;
    }
    return false;
  }

  /**
   * creates new directory
   * @param path path to directory to be created, without leading slash; e.g. 'new-folder'
   */
  public async createDir(path: string) {
    this.client.createDirectory(this.basePath + path);
  }

  /**
   * Uploads a given file to the nextcloud server.
   * @param file The file to be stored
   * @param filePath the filename and path to which the file will be uploaded, no leading slash
   */
  public async uploadFile(file: any, filePath: string): Promise<void> {
    return this.client.putFileContents(
      this.basePath + filePath,
      file
      // { onUploadProgress: progress => {  console.log(`Uploaded ${progress.loaded} / ${progress.total} bytes`); } },
    );
  }

  /**
   * Returns a Promise which resolves as an ArrayBuffer of the file located at the given path
   * @param filePath the filename and path relative to the base path without leading slash; e.g. 'photos/105.png'
   */
  public async getFile(filePath: string): Promise<SafeUrl> {
    let file;
    try {
      file = await this.client.getFileContents(this.basePath + filePath);
      return this.bufferArrayToBase64(file);
    } catch (err) {
      return Promise.reject("Could not load file.");
    }
  }

  /**
   * converts an ArrayBuffer to a SafeUrl and returns it
   * @param arrayBuffer ArrayBuffer to be converted
   */
  private bufferArrayToBase64(arrayBuffer: ArrayBuffer): SafeUrl {
    const base64String = btoa(
      new Uint8Array(arrayBuffer).reduce((data, byte) => {
        return data + String.fromCharCode(byte);
      }, "")
    );
    return this.domSanitizer.bypassSecurityTrustUrl(
      "data:image/jpg;base64," + base64String
    );
  }

  /**
   * Check whether the client is connected and therefore this service is able to execute its remote actions.
   */
  public isConnected() {
    return !!this.client;
  }
}

import { Injectable } from "@angular/core";
import config from "./config-fix.json";

@Injectable({
  providedIn: "root",
})
export class ConfigService {
  constructor() {}

  public async loadConfig() {
    return await new Promise((resolve) => {
      setTimeout(() => {
        console.log("config loaded"), resolve();
      }, 300);
    });
  }

  public getConfig<T>(id: string): T {
    return config[id];
  }

  public getAllConfigs<T>(prefix: string): T[] {
    const matchingConfigs = [];

    for (const id of Object.keys(config)) {
      if (id.startsWith(prefix)) {
        config[id]._id = id;
        matchingConfigs.push(config[id]);
      }
    }

    return matchingConfigs;
  }
}

