import { MeetingNoteAttendance } from "../meeting-note-attendance";
import { DatabaseEntity } from "../../../core/entity/database-entity.decorator";
import { Entity } from "../../../core/entity/entity";
import { DatabaseField } from "../../../core/entity/database-field.decorator";
import { WarningLevel, WarningLevelColor } from "../../warning-level";
import { InteractionType } from "../note-config-loader/note-config.interface";

@DatabaseEntity("Note")
export class Note extends Entity {
  /** IDs of Child entities linked with this note */
  @DatabaseField() children: string[] = [];
  /** An array of triplets containing information about the child and it's attendance */
  @DatabaseField() attendances: MeetingNoteAttendance[] = [];
  @DatabaseField() date: Date;
  @DatabaseField() subject: string = "";
  @DatabaseField() text: string = "";
  @DatabaseField() author: string = "";
  @DatabaseField({ dataType: "interaction-type" }) category: InteractionType = {
    name: "",
  };
  @DatabaseField({ dataType: "string" }) warningLevel: WarningLevel =
    WarningLevel.OK;

  getWarningLevel(): WarningLevel {
    return this.warningLevel;
  }

  // TODO: color logic should not be part of entity/model but rather in the component responsible for displaying it
  public getColor() {
    if (this.warningLevel === WarningLevel.URGENT) {
      return WarningLevelColor(WarningLevel.URGENT);
    }
    if (this.warningLevel === WarningLevel.WARNING) {
      return WarningLevelColor(WarningLevel.WARNING);
    }

    const color = this.category.color;
    return color ? color : "";
  }

  public getColorForId(entityId: string) {
    if (this.category.isMeeting && !this.isPresent(entityId)) {
      // child is absent, highlight the entry
      return WarningLevelColor(WarningLevel.URGENT);
    }
    return this.getColor();
  }

  /**
   * whether a specific child with given childId is linked to this not
   * @param childId The childId to check for
   */
  isLinkedWithChild(childId: string): boolean {
    return this.children.includes(childId);
  }

  /**
   * returns the children that were either present or absent
   * @param presence true to get the children that were present, false to get the children that were absent
   */
  childrenWithPresence(presence: boolean): MeetingNoteAttendance[] {
    return this.attendances.filter(
      (attendance) => attendance.present === presence
    );
  }

  /**
   * whether or not a specific child was present or not.
   * This does not check whether or not this note is a meeting and will not return
   * a sensible value, if this child couldn't be found
   * @param childId The id of the child to check for
   */
  isPresent(childId: string): boolean {
    const child = this.attendances.find(
      (attendance) => attendance.childId === childId
    );
    if (child !== undefined) {
      return child.present;
    }
  }

  /**
   * removes a specific child from this note
   * @param childId The id of the child to exclude from the notes
   */
  removeChild(childId: string) {
    this.children.splice(this.children.indexOf(childId), 1);
    this.attendances.splice(
      this.attendances.findIndex(
        (attendance) => attendance.childId === childId
      ),
      1
    );
  }

  /**
   * adds a new child to this note
   * @param childId The id of the child to add to the notes
   */
  addChild(childId: string) {
    this.children.splice(0, 0, childId);
    this.attendances.splice(0, 0, new MeetingNoteAttendance(childId));
  }

  /**
   * adds multiple children to this note
   * @param childIds The id's of the children to add
   */
  addChildren(...childIds: string[]) {
    childIds.forEach((child) => this.addChild(child));
  }

  /**
   * Toggles for a given child it's presence.
   * If the child was absent, the presence-field will be true for that child.
   * If the child was present, the presence-field will be false for that child
   * @param childId The ID of the child
   */
  togglePresence(childId: string) {
    const child = this.attendances.find(
      (attendance) => attendance.childId === childId
    );
    if (child !== undefined) {
      child.present = !child.present;
    }
  }
}

import { SessionService } from "./session.service";
import { User } from "app/core/user/user";
import { StateHandler } from "../session-states/state-handler";
import { ConnectionState } from "../session-states/connection-state.enum";
import { LoginState } from "../session-states/login-state.enum";
import { SyncState } from "../session-states/sync-state.enum";
import { Database } from "app/core/database/database";
import { EntitySchemaService } from "app/core/entity/schema/entity-schema.service";
import { RemoteSession } from "./remote-session";
import { PouchDatabase } from "../../database/pouch-database";
import { AlertService } from "../../alerts/alert.service";
import { LoggingService } from "../../logging/logging.service";

/**
 * SessionService implementation for use of the app with direct requests to the remote server
 * avoiding any sync to local data.
 *
 * This can be a useful mode for users to save time (no waiting till initial sync is complete)
 * or when working on a device that the user is not regularly using (e.g. a public computer)
 * where data should not be saved locally for security reasons also.
 *
 * The OnlineSessionService mode is not usable without an internet connection. Offline functionality is not available.
 *
 * TODO: requires a configuration or UI option to select OnlineSession
 *
 * For an CouchDB/PouchDB sync based session implementation that allows offline use see {@link SyncedSessionService}
 */
export class OnlineSessionService extends SessionService {
  private currentUser: User;
  private loginState: StateHandler<LoginState> = new StateHandler<LoginState>(
    LoginState.LOGGED_OUT
  );
  private connectionState: StateHandler<ConnectionState> = new StateHandler<
    ConnectionState
  >(ConnectionState.DISCONNECTED);
  private syncState: StateHandler<SyncState> = new StateHandler<SyncState>(
    SyncState.UNSYNCED
  );
  private remoteSession: RemoteSession;
  private database: PouchDatabase;

  constructor(
    private alertService: AlertService,
    private loggingService: LoggingService,
    private entitySchemaService: EntitySchemaService
  ) {
    super();
    this.remoteSession = new RemoteSession();
    this.database = new PouchDatabase(
      this.remoteSession.database,
      this.alertService,
      this.loggingService
    );
  }

  /** see {@link SessionService} */
  public getCurrentUser(): User {
    return this.currentUser;
  }

  /** see {@link SessionService} */
  public isLoggedIn(): boolean {
    return this.loginState.getState() === LoginState.LOGGED_IN;
  }

  /** see {@link SessionService} */
  public getConnectionState(): StateHandler<ConnectionState> {
    return this.connectionState;
  }

  /** see {@link SessionService} */
  public getLoginState(): StateHandler<LoginState> {
    return this.loginState;
  }

  /** see {@link SessionService} */
  public getSyncState(): StateHandler<SyncState> {
    return this.syncState;
  }

  /** see {@link SessionService} */
  public getDatabase(): Database {
    return this.database;
  }

  /**
   * Log in the given user authenticating against the remote server's CouchDB.
   *
   * also see {@link SessionService}
   */
  public async login(username, password): Promise<LoginState> {
    const connectionState: ConnectionState = await this.remoteSession.login(
      username,
      password
    );
    if (connectionState === ConnectionState.CONNECTED) {
      this.currentUser = await this.loadUser(username);

      this.loginState.setState(LoginState.LOGGED_IN);
      this.connectionState.setState(ConnectionState.CONNECTED);
      this.syncState.setState(SyncState.COMPLETED);

      return LoginState.LOGGED_IN;
    }
    return LoginState.LOGIN_FAILED;
  }

  /** see {@link SessionService} */
  public logout(): void {
    this.remoteSession.logout();

    this.loginState.setState(LoginState.LOGGED_OUT);
    this.connectionState.setState(ConnectionState.DISCONNECTED);
  }

  /**
   * Dummy implementation, will directly go to SyncState.COMPLETED
   * OnlineSession does not require any kind of synchronisation.
   */
  public async sync(): Promise<any> {
    this.syncState.setState(SyncState.COMPLETED);
  }

  /**
   * Helper to get a User Entity from the Database without needing the EntityMapperService
   * @param userId Id of the User to be loaded
   */
  private async loadUser(userId: string): Promise<User> {
    const user = new User("");
    const userData = await this.getDatabase().get("User:" + userId);
    this.entitySchemaService.loadDataIntoEntity(user, userData);
    return user;
  }
}

import { Database } from "./database";
import { AlertService } from "../alerts/alert.service";
import { AlertDisplay } from "../alerts/alert-display";
import moment from "moment";
import { LoggingService } from "../logging/logging.service";

/**
 * Wrapper for a PouchDB instance to decouple the code from
 * that external library.
 *
 * Additional convenience functions on top of the PouchDB API
 * should be implemented in the abstract {@link Database}.
 */
export class PouchDatabase extends Database {
  /**
   * Create a PouchDB database manager.
   * @param _pouchDB An (initialized) PouchDB database instance from the PouchDB library.
   * @param alertService The AlertService instance of the app to be able to display problems to users.
   * @param loggingService The LoggingService instance of the app to log and report problems.
   */
  constructor(
    private _pouchDB: any,
    private alertService: AlertService,
    private loggingService: LoggingService
  ) {
    super();
  }

  /**
   * Load a single document by id from the database.
   * (see {@link Database})
   * @param id The primary key of the document to be loaded
   * @param options Optional PouchDB options for the request
   * @param returnUndefined (Optional) return undefined instead of throwing error if doc is not found in database
   */
  get(id: string, options: any = {}, returnUndefined?: boolean): Promise<any> {
    return this._pouchDB.get(id, options).catch((err) => {
      if (err.status === 404) {
        this.loggingService.debug("Doc not found in database: " + id);
        if (returnUndefined) {
          return undefined;
        } else {
          throw err;
        }
      } else {
        this.notifyError(err);
        throw err;
      }
    });
  }

  /**
   * Load all documents (matching the given PouchDB options) from the database.
   * (see {@link Database})
   *
   * Normally you should rather use "getAll()" or another well typed method of this class
   * instead of passing PouchDB specific options here
   * because that will make your code tightly coupled with PouchDB rather than any other database provider.
   *
   * @param options PouchDB options object as in the normal PouchDB library
   */
  allDocs(options?: any) {
    return this._pouchDB.allDocs(options).then((result) => {
      const resultArray = [];
      for (const row of result.rows) {
        resultArray.push(row.doc);
      }
      return resultArray;
    });
  }

  /**
   * Save a document to the database.
   * (see {@link Database})
   *
   * @param object The document to be saved
   * @param forceOverwrite (Optional) Whether conflicts should be ignored and an existing conflicting document forcefully overwritten.
   */
  put(object: any, forceOverwrite?: boolean) {
    const options: any = {};
    // if (forceOverwrite) {
    //   options.force = true;
    // }

    return this._pouchDB.put(object, options).catch((err) => {
      if (err.status === 409) {
        this.resolveConflict(object, forceOverwrite, err);
      } else {
        this.notifyError(err);
        throw err;
      }
    });
  }

  /**
   * Delete a document from the database
   * (see {@link Database})
   *
   * @param object The document to be deleted (usually this object must at least contain the _id and _rev)
   */
  remove(object: any) {
    return this._pouchDB.remove(object).catch((err) => {
      this.notifyError(err);
      throw err;
    });
  }

  /**
   * Query data from the database based on a more complex, indexed request.
   * (see {@link Database})
   *
   * This is directly calling the PouchDB implementation of this function.
   * Also see the documentation there: {@link https://pouchdb.com/api.html#query_database}
   *
   * @param fun The name of a previously saved database index
   * @param options Additional options for the query, like a `key`. See the PouchDB docs for details.
   */
  query(
    fun: string | ((doc: any, emit: any) => void),
    options: any
  ): Promise<any> {
    return this._pouchDB.query(fun, options);
  }

  /**
   * Create a database index to `query()` certain data more efficiently in the future.
   * (see {@link Database})
   *
   * Also see the PouchDB documentation regarding indices and queries: {@link https://pouchdb.com/api.html#query_database}
   *
   * @param designDoc The PouchDB style design document for the map/reduce query
   */
  async saveDatabaseIndex(designDoc: any): Promise<any> {
    const existingDesignDoc = await this.get(designDoc._id, {}, true);
    if (!existingDesignDoc) {
      this.loggingService.debug("creating new database index");
    } else if (
      JSON.stringify(existingDesignDoc.views) !==
      JSON.stringify(designDoc.views)
    ) {
      this.loggingService.debug("replacing existing database index");
      designDoc._rev = existingDesignDoc._rev;
    } else {
      // already up to date, nothing more to do
      return;
    }

    await this.put(designDoc);

    await this.prebuildViewsOfDesignDoc(designDoc);
  }

  private async prebuildViewsOfDesignDoc(designDoc: any) {
    for (const viewName of Object.keys(designDoc.views)) {
      try {
        const queryName =
          designDoc._id.replace(/_design\//, "") + "/" + viewName;
        console.log("start indexing " + queryName);
        const startTime = moment();
        await this.query(queryName, { key: "1" });
        const indexingTime = moment().diff(startTime, "milliseconds");
        console.log("done indexing " + queryName, indexingTime);
        if (indexingTime > 1000) {
          this.loggingService.warn({
            action: "Indexing for query",
            query: queryName,
            duration: indexingTime,
          });
        }
      } catch (err) {
        this.alertService.addWarning(
          "failed to trigger query for new index: ",
          err
        );
      }
    }
  }

  private notifyError(err) {
    this.alertService.addWarning(
      "PouchDB Error " + err.status + ": " + JSON.stringify(err),
      AlertDisplay.NONE
    );
  }

  /**
   * Attempt to intelligently resolve conflicting document versions automatically.
   * @param newObject
   * @param overwriteChanges
   * @param existingError
   */
  private resolveConflict(
    newObject: any,
    overwriteChanges: boolean,
    existingError: any
  ) {
    this.get(newObject._id).then((existingObject) => {
      const resolvedObject = this.mergeObjects(existingObject, newObject);
      if (resolvedObject) {
        this.alertService.addDebug(
          "resolved document conflict automatically (" +
            resolvedObject._id +
            ")"
        );
        this.put(resolvedObject);
      } else if (overwriteChanges) {
        this.alertService.addDebug(
          "overwriting conflicting document version (" + newObject._id + ")"
        );
        newObject._rev = existingObject._rev;
        this.put(newObject);
      } else {
        existingError.message = existingError.message + " (unable to resolve)";
        throw existingError;
      }
    });
  }

  private mergeObjects(existingObject: any, newObject: any) {
    // TODO: implement automatic merging of conflicting entity versions
    return undefined;
  }
}

